INSERT INTO `custom_texts` VALUES ('-2006000', 'Who is the Killer startet in %u...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006001', 'Das Spiel beginnt...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006002', 'Du hast keinen freien Platz in deinem Rucksack!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006003', 'Du hast %u Goldmünzen bekommen!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
