INSERT INTO `custom_texts` VALUES ('-2006006', 'Du hast 1 Eventmünze bekommen!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006007', '%s ist gekillt worden...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006008', '%s ist besiegt worden...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006009', 'Du hast die Spielzone verlassen. Du solltest umkehren, oder du wirst in %u Sek. teleportiert.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006010', 'Du bist ein Killer, beseitige diese nervigen Camper!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006011', 'Benutze den Befehl \".fangen\" um einen angeklickten Camper zu fangen, und danach den Befehl \".killen\" um ihn zu ermorden.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006012', 'Du musst warten, bis alle Camper abgestimmt haben.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006013', 'Du wurdest erkannt!! Laufe zum Teleportfelsen (Siehe Kartenmarkierung), um zu entkommen!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006014', 'Wenn du diesen Sound hörst, dann hast du ein neues Signal von einem Camper auf der Karte bekommen.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006015', 'Verwirre deine Opfer, sodass sie glauben du bist einer von ihnen.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006016', 'Benutze den Befehl \".verwirren\" um die Camper mit deinem Tot zu verwirren, oder den Befehl \".wechseln\" um dich als Camper zu tarnen.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006017', 'Du hast %u Sek. Zeit zum Abstimmen!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006018', 'Du hast den falschen Spieler für einen Killer gehalten! Damit bist du aus dem Spiel ausgeschieden.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006019', 'Du hast einen Killer erkannt! Er ist jetzt auf dem Weg zum Teleportfelsen (Siehe Kartemarkierung), du musst ihn aufhalten!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006020', 'Du bist ein Camper, überlebe bis die Zeit abgelaufen ist, und finde heraus wer ein Killer ist.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006021', 'Wenn du die gruselige Musik hörst, ist ein Killer nicht weit entfernt von dir.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `custom_texts` VALUES ('-2006022', 'Killer sind unsichtbar, können aber die Gestalt von einem Camper annehmen, lass dich nicht täuschen!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
